import { sortByUpvotesAPI } from "../actions/suggestions";
import { fetchSortedSuggestions } from "../api";

export default (suggestions = [], action) => {
  switch (action.type) {
    case "FETCH_ALL":
      return action.payload;
    case "CREATE":
      return suggestions;
    case "FETCH_SORTED":
      let sortedSuggestions = [...suggestions];
      action.payload.forEach((suggestion) => {
        sortedSuggestions[suggestion.id] = suggestion;
      });
      return fetchSortedSuggestions;
    default:
      return suggestions;
  }
};
