export default (suggestions = [], action) => {
  switch (action.type) {
    case "FETCH_ALL":
      return action.payload;
    case "CREATE":
      return suggestions;
    case "FETCH_SORTED":
      return suggestions.map((suggestion) =>
        suggestion.id === action.payload.id ? action.payload : suggestion
      );
    default:
      return suggestions;
  }
};
