export default (suggestions = [], action) => {
  switch (action.type) {
    case "FETCH_ALL":
      return action.payload;
    case "CREATE":
      return suggestions;
    case "SORT_BY_UPVOTES":
      let sortByPriceState = Object.assign({}, state);
      let sortedPriceArr =
        action.payload.direction === "asc"
          ? sortAsc(state.filteredProducts, "price")
          : sortDesc(state.filteredProducts, "price");

      return sortByPriceState;
    default:
      return suggestions;
  }
};

function sortAsc(arr, field) {
  return arr.sort(function (a, b) {
    if (a[field] > b[field]) return 1;

    if (b[field] > a[field]) return -1;

    return 0;
  });
}
