import styles from "./Button.module.css";
import backIcon from "../../../assets/shared/icon-arrow-left.svg";
import { Link } from "react-router-dom";

export default function Button({
  style,
  text,
  form,
  onClick,
  link = false,
  destination,
}) {
  const buttonStyling = `button--${style}`;
  const buttonClassNames = `${styles.button} ${styles[buttonStyling]}`;

  // If link is true, create a new styled router link to the given destination.
  // If style of button is a back button, create a new "Go Back" back button with left arrow icon.
  // Else, create a regular html button.
  if (link === true) {
    return (
      <Link to={destination} className={buttonClassNames}>
        {text}
      </Link>
    );
  } else {
    return (
      <button
        type="submit"
        form={form}
        onClick={onClick}
        className={buttonClassNames}
      >
        {text}
      </button>
    );
  }
}
