export default (state = { posts: [] }, action) => {
  switch (action.type) {
    case "FETCH_ALL":
      return action.payload;
    case "CREATE":
      return state;
    case "FETCH_SORTED":
      let sortedSuggestions = [...state];
      action.payload.forEach((suggestion, index) => {
        sortedSuggestions[index] = suggestion;
      });
      return sortedSuggestions;
    case "FETCH_FILTERED":
      let filterSuggestions = [...state];

      return state;
    default:
      return state;
  }
};
