import React, { useState } from "react";
import styles from "./ReplyButton.module.css";

const ReplyButton = ({ replyTo, parentCommentId }) => {
  const [replyFormActive, setReplyForm] = useState(false);

  const handleReply = (e) => {
    e.preventDefault();
    replyFormActive(true);
  };

  return (
    <button className={styles.reply} onClick={handleReply}>
      Reply
    </button>
  );
};

export default ReplyButton;
