export default (suggestions = [], action) => {
  switch (action.type) {
    case "FETCH_ALL":
      return action.payload;
    case "CREATE":
      return suggestions;
    case "SORT_BY_UPVOTE":
      return suggestions;
    case "FETCH_SORTED":
      return {
        ...suggestions,
        sorted: action.payload,
      };
    default:
      return suggestions;
  }
};
