export default (state = { suggestions: [] }, action) => {
  switch (action.type) {
    case "FETCH_ALL":
      return { ...state, suggestions: action.payload.data };
    case "CREATE":
      return state;
    case "FETCH_SORTED":
      let sortedSuggestions = [...state.suggestions];
      action.payload.forEach((suggestion, index) => {
        sortedSuggestions[index] = suggestion;
      });
      return { ...state, suggestions: sortedSuggestions };
    case "FETCH_FILTERED":
      return { ...state };
    default:
      return state;
  }
};
