import React from "react";
import styles from "./Comment.module.css";
import TextBody from "../../../UI/TextBody/TextBody";
const Comment = ({ user, content, id, replies }) => {
  // Replies objects: content, replyingTo, user
  const commentReplies = replies?.map((reply) => (
    <b>
      {reply.replyingTo} {reply.content}
    </b>
  ));
  return (
    <div className={styles.comment}>
      <div className={styles["comment_header"]}>
        <div className={styles.poster}>
          <p className={styles["poster_name"]}>{user.name}</p>
          <p className={styles["poster_username"]}>{user.username}</p>
        </div>

        <p>Reply</p>
      </div>
      {content}
      {commentReplies}
    </div>
  );
};

export default Comment;
