import styles from "./FormTextInput.module.css";
export default function FormTextInput({ inputId, labelHeading, labelCaption }) {
  return (
    <>
      <label for={inputId} className={styles["caption-bold"]}>
        {labelHeading}
      </label>
      <label for={inputId} className={styles.caption}>
        {labelCaption}
      </label>
      <input className={styles["input"]} type="text" id={inputId} />
    </>
  );
}
