import {
  FETCH_ALL,
  FETCH_SORTED,
  FETCH_FILTERED,
} from "../constants/actionTypes";

const initialState = {
  suggestions: [],
  filterCategory: null,
  sortingOrder: null,
  sortingType: null,
};

export default (state = initialState, action) => {
  switch (action.type) {
    case FETCH_ALL:
      return action.payload;
    case "CREATE":
      return state.suggestions;
    case FETCH_SORTED:
      return action.payload;
    case FETCH_FILTERED:
      return action.payload;
    default:
      return state.suggestions;
  }
};
