import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { createSuggestion } from "../../actions/suggestions";
import styles from "./Create.module.css";
import Heading from "../UI/Heading/Heading";
import Form from "../UI/Forms/Form/Form";
import FormTextInput from "../UI/Forms/FormTextInput/FormTextInput";
import FormSelectInput from "../UI/Forms/FormSelectInput/FormSelectInput";
import createIcon from "../../assets/shared/icon-new-feedback.svg";
import Button from "../UI/Button/Button";

import { SpinnerCircularFixed } from "spinners-react";

export default function Create() {
  const [newSuggestion, setNewSuggestion] = useState({
    title: "",
    category: "Feature",
    upvotes: 0,
    status: "suggestion",
    description: "",
    comments: [],
  });

  const [addBtnText, setAddBtnText] = useState("Add Feedback");

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { isLoading } = useSelector((state) => state.suggestions);

  const handleSubmit = (event) => {
    event.preventDefault();
    setAddBtnText(
      <SpinnerCircularFixed
        color="hsl(230, 76%, 59%)"
        enabled={isLoading}
        secondaryColor="#f2f5ff"
        size={50}
        style={{
          margin: "0 auto",
        }}
      />
    );

    // Category field must be lowercase before being added to the database.
    dispatch(
      createSuggestion({
        ...newSuggestion,
        category: newSuggestion.category.toLowerCase(),
      })
    );

    setTimeout(() => {
      setAddBtnText("Feedback Added");
      navigate("/");
    }, 1000);
  };

  return (
    <Form icon={createIcon} backButtonDestination="/">
      <Heading type="h1">Create New Feedback</Heading>

      <form id="form">
        <FormTextInput
          inputId="title"
          value={newSuggestion.title}
          labelHeading="Feedback Title"
          labelCaption="Add a short, descriptive headline"
          onChange={(event) =>
            setNewSuggestion({ ...newSuggestion, title: event.target.value })
          }
        />
        <FormSelectInput
          inputId="category"
          value={newSuggestion.category}
          labelHeading="Category"
          labelCaption="Choose a category for your feedback"
          onChange={(event) =>
            setNewSuggestion({ ...newSuggestion, category: event.target.value })
          }
        />
        <FormTextInput
          inputId="detail"
          value={newSuggestion.description}
          large="true"
          labelHeading="Feedback Detail"
          labelCaption="Include any specific comments on what should be improved, added, etc."
          onChange={(event) =>
            setNewSuggestion({
              ...newSuggestion,
              description: event.target.value,
            })
          }
        />

        <div className={styles["button__container"]}>
          <Button
            style="violet"
            text={addBtnText}
            form="form"
            onClick={handleSubmit}
          />

          <Button
            style="navy-blue"
            text="Cancel"
            onClick={handleSubmit}
            link={true}
            destination="/"
          />
        </div>
      </form>
    </Form>
  );
}
