import {
  FETCH_ALL,
  FETCH_FILTERED,
  CREATE_SUGGESTION,
  START_LOADING,
  END_LOADING,
} from "../constants/actionTypes";
export default (state = [], action) => {
  switch (action.type) {
    case START_LOADING:
      return { ...state, isLoading: true };
    case END_LOADING:
      return { ...state, isLoading: false };
    case FETCH_ALL:
      return { ...state, suggestions: action.payload.data };
    case CREATE_SUGGESTION:
      return {
        ...state,
        suggestions: [...state, action.payload],
      };
    case FETCH_FILTERED:
      return { ...state, suggestions: action.payload };
    default:
      return state;
  }
};
