import { bindActionCreators } from "redux";
import {
  FETCH_ALL,
  FETCH_SORTED,
  FETCH_FILTERED,
} from "../constants/actionTypes";
export default (suggestions = [], action) => {
  switch (action.type) {
    case FETCH_ALL:
      return action.payload;
    case "CREATE":
      return suggestions;
    case FETCH_SORTED:
      const sortedSuggestions = [...suggestions];

      return action.payload.filter((fetchedSuggestion) => {
        sortedSuggestions.some(
          (suggestion) => fetchedSuggestion["_id"] === suggestion["_id"]
        );
      });
    case FETCH_FILTERED:
      const filteredSuggestions = [...suggestions];

      return filteredSuggestions.filter((suggestion) =>
        action.payload.some(
          (fetchedSuggestion) => fetchedSuggestion["_id"] === suggestion["_id"]
        )
      );
    default:
      return suggestions;
  }
};
