export default (suggestions = [], action) => {
  switch (action.type) {
    case "FETCH_ALL":
      return action.payload;
    case "CREATE":
      return suggestions;
    case "FETCH_SORTED":
      // let sortedSuggestions = [...suggestions];
      // action.payload.forEach((suggestion) => {
      //   sortedSuggestions[suggestion.id] = suggestion;
      // });
      return [
        ...suggestions,
        {
          ...action.payload,
        },
      ];
    default:
      return suggestions;
  }
};
