import React, { useState } from "react";
import Form from "../UI/Forms/Form/Form";
import Heading from "../UI/Heading/Heading";
import FormTextInput from "../UI/Forms/FormTextInput/FormTextInput";
import addIcon from "../../assets/shared/icon-new-feedback.svg";
import Button from "../UI/Button/Button";

const Auth = () => {
  const [isSignup, setIsSignup] = useState(false);
  const [newUser, setNewUser] = useState({
    email: "",
    password: "",
  });

  const handleSubmit = () => {
    console.log("submit");
  };

  const heading = isSignup ? "Create An Account" : "Sign In";

  return (
    <Form icon={addIcon} backButtonDestination="/">
      <Heading type="h1">{heading}</Heading>

      <form id="create-user">
        <FormTextInput
          inputId="email"
          value={newUser.email}
          labelHeading="Email Address"
          onChange={(event) =>
            setNewUser({ ...newUser, email: event.target.value })
          }
        />
        <FormTextInput
          inputId="password"
          value={newUser.password}
          labelHeading="Password"
          labelCaption="Password must be at least 8 characters"
          onChange={(event) =>
            setNewUser({ ...newUser, password: event.target.value })
          }
        />

        <Button
          style="violet"
          text="Login"
          form="create-user"
          onClick={handleSubmit}
        />
        <Button style="blue" text="Sign up" onClick={() => setIsSignup(true)} />
      </form>
    </Form>
  );
};

export default Auth;
