import axios from "axios";

const API = axios.create({ baseURL: "http://localhost:5000" });

const url = "/suggestions";

export const fetchSuggestions = () => API.get(url);

export const fetchFilteredSuggestions = (category, type, order) =>
  API.get(`${url}/filter?category=${category}&type=${type}&order=${order}`);

export const createSuggestion = (newSuggestion) => API.post(url, newSuggestion);

export const upvoteSuggestion = (id) => API.patch(`${url}/${id}/upvote`);
