import {
  FETCH_ALL,
  FETCH_SORTED,
  FETCH_FILTERED,
} from "../constants/actionTypes";

const initialState = {
  suggestions: [],
  filterCategory: "all",
  sortingOrder: "desc",
  sortingType: "upvotes",
};

export default (state = initialState, action) => {
  switch (action.type) {
    case FETCH_ALL:
      return { ...state, suggetions: action.payload };
    case "CREATE":
      return state.suggestions;
    case FETCH_SORTED:
      return { ...state, suggestions: action.payload };
    case FETCH_FILTERED:
      return action.payload;
    case "SET_SORTING":
      const { dataType, order } = action.payload;
      return { ...state, sortingType: dataType, sortingOrder: order };
    default:
      return state.suggestions;
  }
};
