import styles from "./SuggestionsFeed.module.css";
import { useSelector, useDispatch } from "react-redux";
// import { sortByUpvotes } from "../../../actions/suggestions";
import Suggestion from "./Suggestion/Suggestion";
export default function SuggestionsFeed() {
  // Retrieves all of the suggestions
  const suggestions = useSelector((state) => state.suggestions);
  const dispatch = useDispatch();

  const sortByMostUpvotes = (e) => {
    let input = "Most Upvotes".toLowerCase();
    let direction = input.contains("most") ? "desc" : "asc";

    if (input.contains("upvotes")) {
      dispatch(sortByUpvotes(direction));
    }
  };

  return (
    <section className={styles.suggestions}>
      {suggestions.map((suggestion) => (
        <Suggestion key={suggestion["_id"]} suggestionData={suggestion} />
      ))}
    </section>
  );
}
