import styles from "./Headings.module.css";
import classNames from "classnames";

const Heading = ({ type, children }) => {
  // This allows us to set the HTML tag depending on the value of type.
  const HeadingLevel = type;

  // Allows us to select the CSS based
  const headingSelector = `heading--${type}`;
  const headingStyling = `${styles.heading} ${styles[headingSelector]}`;
  return (
    <HeadingLevel
      className={classNames(styles.heading, styles[headingSelector])}
    >
      {children}
    </HeadingLevel>
  );
};

export default Heading;
