  //   </section>
  // ) : (
  //   <section className={styles["suggestions--empty"]}>
  //     <NoSuggestions />
  //   </section>
  // );

  const suggestionsFeed = suggestions.map((suggestion) => (
    <Suggestion key={suggestion["_id"]} suggestionData={suggestion} />
  ));

  const suggestionsFeedEmpty = <NoSuggestions />;

  return (
    <section
      className={
        suggestions.length !== 0
          ? styles.suggestions
          : styles["suggetions--empty"]
      }
    ></section>
  );
}
